        [HttpGet]
        public HttpResponseMessage ValidarUsuario(string t)
        {

            Usuario usuario = db.Usuario.Where(r => r.Token.Equals(t)).FirstOrDefault();

            usuario.Estado = 1;

            try
            {
                db.Entry(usuario).State = EntityState.Modified;

                db.SaveChanges();
            }
            catch (Exception)
            {

            }

            var response = Request.CreateResponse(HttpStatusCode.Moved);
            response.Headers.Location = new Uri("http://foodsinall.tk");
            return response;
        }

        [HttpPost]
        public bool ModificarUsuario(JObject datos)
        {

            var q = false;

            Usuario usuario = db.Usuario.Find(Convert.ToInt32(datos.GetValue("Id").ToString()));

            //var hash = System.Web.Helpers.Crypto.Hash(datos.GetValue("Contrasena").ToString());

            usuario.Nombre_y_Apellidos = datos.GetValue("ApNom").ToString();
            usuario.Telefono = (int)datos.GetValue("Telefono");
            usuario.Foto = Encoding.ASCII.GetBytes(datos.GetValue("Foto").ToString());
            //usuario.Contrasena = hash;

            try
            {
                db.Entry(usuario).State = EntityState.Modified;

                db.SaveChanges();

                q = true;
            }
            catch (Exception)
            {

            }

            return q;
        }

        [HttpGet]
        public IHttpActionResult EliminarUsuario(int id)
        {
            Usuario usuario = db.Usuario.Find(id);
            if (usuario == null)
            {
                return NotFound();
            }

            db.Usuario.Remove(usuario);
            db.SaveChanges();

            return Ok(usuario);
        }

        [HttpGet]
        private bool UsuarioExiste(string email)
        {
            return db.Usuario.Count(e => e.Email.Equals(email)) < 1;
        }

        [HttpGet]
        public bool SubirImagenes()
        {
            var server = String.Format("{0}/Foodsinall/{1}", "ftp://lacicloud.net", "imagen2.png");

            var user = "jalorrados ";

            var pass = "jalorrados1937";

            var request = WebRequest.Create(server);

            request.Method = WebRequestMethods.Ftp.UploadFile;

            request.Credentials = new NetworkCredential(user, pass);

            var sourceStream = new MemoryStream(Convert.FromBase64String(""));

            byte[] fileContents = sourceStream.ToArray();

            sourceStream.Close();

            request.ContentLength = fileContents.Length;

            var requestStream = request.GetRequestStream();

            requestStream.Write(fileContents, 0, fileContents.Length);

            requestStream.Close();

            var response = (FtpWebResponse)request.GetResponse();

            var estado = response.StatusDescription;

            response.Close();

            return true;
        }
